long long minimumCost(int* nums, int numsSize) {
    long long sum = 0;
    for (int i = 0; i < numsSize; ++i) {
        sum += nums[i];
    }
    long long avg = sum / numsSize;
    long long closestPalindrome = avg;
    for (long long p = avg; p <= avg + 2; ++p) {
        if (isPalindrome(p)) {
            closestPalindrome = p;
            break;
        }
    }
    for (long long p = avg - 1; p >= avg - 2; --p) {
        if (isPalindrome(p)) {
            closestPalindrome = p;
            break;
        }
    }
    long long cost = 0;
    for (int i = 0; i < numsSize; ++i) {
        cost += abs(nums[i] - closestPalindrome);
    }
    return cost;
}

int isPalindrome(long long x) {
    if (x < 0) return 0;
    long long rev = 0, original = x;
    while (x != 0) {
        rev = rev * 10 + x % 10;
        x /= 10;
    }
    return original == rev;
}
