class Solution {
  bool _isPalindromic(int n) {
    String s = n.toString();
    for (int i = 0; i < s.length ~/ 2; i++) {
      if (s[i] != s[s.length - 1 - i]) return false;
    }
    return true;
  }

  int _calculateCost(List<int> nums, int target) {
    int cost = 0;
    for (int num in nums) {
      cost += (num - target).abs();
    }
    return cost;
  }

  int minimumCost(List<int> nums) {
    nums.sort();
    int median = nums[nums.length ~/ 2];
    int cost = _calculateCost(nums, median);
    if (_isPalindromic(median)) return cost;

    // Try to adjust median to the closest palindromic number
    int lower = median, upper = median;
    while (!_isPalindromic(lower) && lower > 0) lower--;
    while (!_isPalindromic(upper) && upper < 1e9.toInt()) upper++;

    if (lower > 0 && _isPalindromic(lower)) {
      cost = min(cost, _calculateCost(nums, lower));
    }
    if (upper < 1e9.toInt() && _isPalindromic(upper)) {
      cost = min(cost, _calculateCost(nums, upper));
    }
    return cost;
  }

  int min(int a, int b) => a < b ? a : b;
}
