class Solution {
    public long minimumCost(int[] nums) {
        long minCost = Long.MAX_VALUE;
        int maxNum = 0;
        // Find the maximum number in the array to limit the search for palindromic numbers.
        for (int num : nums) {
            maxNum = Math.max(maxNum, num);
        }
        // Generate all palindromic numbers up to the maximum number in the array.
        for (int i = 1; i <= maxNum; i++) {
            if (isPalindrome(i)) {
                long cost = 0;
                for (int num : nums) {
                    cost += Math.abs(num - i);
                }
                minCost = Math.min(minCost, cost);
            }
        }
        return minCost;
    }

    private boolean isPalindrome(int x) {
        if (x < 0) return false;
        int reversed = 0, original = x;
        while (x != 0) {
            reversed = reversed * 10 + x % 10;
            x /= 10;
        }
        return original == reversed;
    }
}
