class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function minimumCost($nums) {
        $n = count($nums);
        $median = $this->findMedian($nums, 0, $n - 1, intdiv($n, 2) + $n % 2);
        $cost = 0;
        foreach ($nums as $num) {
            $cost += abs($num - $median);
        }
        return $cost;
    }

    function findMedian(&$nums, $left, $right, $k) {
        if ($left == $right) return $nums[$left];
        $pivotIndex = $this->partition($nums, $left, $right);
        if ($k == $pivotIndex + 1) {
            return $nums[$pivotIndex];
        } elseif ($k < $pivotIndex + 1) {
            return $this->findMedian($nums, $left, $pivotIndex - 1, $k);
        } else {
            return $this->findMedian($nums, $pivotIndex + 1, $right, $k);
        }
    }

    function partition(&$nums, $left, $right) {
        $pivot = $nums[$right];
        $i = $left;
        for ($j = $left; $j <= $right - 1; $j++) {
            if ($nums[$j] <= $pivot) {
                $this->swap($nums, $i, $j);
                $i++;
            }
        }
        $this->swap($nums, $i, $right);
        return $i;
    }

    function swap(&$nums, $i, $j) {
        $temp = $nums[$i];
        $nums[$i] = $nums[$j];
        $nums[$j] = $temp;
    }
}
