class Solution(object):
    def minimumCost(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        def is_palindrome(n):
            return str(n) == str(n)[::-1]

        nums.sort()
        mid = len(nums) // 2
        target = nums[mid]
        if is_palindrome(target):
            return sum(abs(num - target) for num in nums)

        # Find the closest palindromic number
        lower, upper = target, target
        while not is_palindrome(lower) and not is_palindrome(upper):
            lower -= 1
            upper += 1
            if lower < 0:  # Ensure we don't go into negative numbers
                lower = 0

        if is_palindrome(lower) and is_palindrome(upper):
            lower_cost = sum(abs(num - lower) for num in nums)
            upper_cost = sum(abs(num - upper) for num in nums)
            return min(lower_cost, upper_cost)
        elif is_palindrome(lower):
            return sum(abs(num - lower) for num in nums)
        else:
            return sum(abs(num - upper) for num in nums)
