from typing import List

class Solution:
    def minimumCost(self, nums: List[int]) -> int:
        def is_palindrome(n):
            return str(n) == str(n)[::-1]

        def closest_palindrome(n):
            # Find the closest palindromic number to n
            if is_palindrome(n):
                return n
            for i in range(n-1, 0, -1):
                if is_palindrome(i):
                    lower = i
                    break
            for i in range(n+1, 10**9):
                if is_palindrome(i):
                    upper = i
                    break
            return lower if n - lower <= upper - n else upper

        median = sorted(nums)[len(nums) // 2]  # Find median for minimal adjustment
        target = closest_palindrome(median)  # Find closest palindromic number to median
        return sum(abs(num - target) for num in nums)  # Calculate total cost
