# @param {Integer[]} nums
# @return {Integer}
def minimum_cost(nums)
    nums.sort!
    median = nums[nums.size / 2]
    cost = nums.reduce(0) {|sum, num| sum + (num - median).abs}

    # Check if adjusting to a nearby palindromic number reduces the cost
    lower_palindrome = median.downto(1).find {|x| x.to_s == x.to_s.reverse}
    upper_palindrome = median.upto(1_000_000_000).find {|x| x.to_s == x.to_s.reverse}
    lower_cost = nums.reduce(0) {|sum, num| sum + (num - lower_palindrome).abs}
    upper_cost = nums.reduce(0) {|sum, num| sum + (num - upper_palindrome).abs}

    [cost, lower_cost, upper_cost].min
end
