function minimumCost(nums: number[]): number {
    // Helper function to check if a number is palindromic
    function isPalindromic(n: number): boolean {
        const str = n.toString();
        let left = 0, right = str.length - 1;
        while (left < right) {
            if (str[left] !== str[right]) return false;
            left++;
            right--;
        }
        return true;
    }

    // Find the median of the array to minimize the cost
    nums.sort((a, b) => a - b);
    let median = nums[Math.floor(nums.length / 2)];

    // Adjust the median to the nearest palindromic number if it's not already one
    if (!isPalindromic(median)) {
        let lower = median, upper = median;
        // Search downwards for the closest palindromic number
        while (!isPalindromic(lower) && lower > 0) lower--;
        // Search upwards for the closest palindromic number
        while (!isPalindromic(upper)) upper++;
        // Choose the closest palindromic number to the median
        median = (median - lower <= upper - median) ? lower : upper;
    }

    // Calculate the total cost to convert all numbers to the chosen palindromic number
    return nums.reduce((acc, cur) => acc + Math.abs(cur - median), 0);
}
