public class Solution {
    public long MinSum(int[] nums1, int[] nums2) {
        long sum1 = 0, sum2 = 0;
        foreach (var num in nums1) sum1 += num;
        foreach (var num in nums2) sum2 += num;

        int zeroCount1 = 0, zeroCount2 = 0;
        foreach (var num in nums1) if (num == 0) zeroCount1++;
        foreach (var num in nums2) if (num == 0) zeroCount2++;

        if (zeroCount1 + zeroCount2 == 0) return sum1 == sum2 ? sum1 : -1;

        long totalZeros = zeroCount1 + zeroCount2;
        long minSum = Math.Max(sum1, sum2);
        long diff = Math.Abs(sum1 - sum2);

        if ((zeroCount1 == 0 && sum1 < sum2) || (zeroCount2 == 0 && sum2 < sum1)) {
            return -1;
        }

        if (diff % totalZeros == 0) {
            return minSum + diff;
        } else {
            long additionalSumNeeded = totalZeros - (diff % totalZeros);
            return minSum + diff + additionalSumNeeded;
        }
    }
}
