public class Solution {
    public long MinSum(int[] nums1, int[] nums2) {
        long sum1 = 0, sum2 = 0;
        int zeros1 = 0, zeros2 = 0;
        foreach (var num in nums1) {
            sum1 += num;
            if (num == 0) zeros1++;
        }
        foreach (var num in nums2) {
            sum2 += num;
            if (num == 0) zeros2++;
        }

        // Impossible to make sums equal if one array has zeros and the other has a larger sum without zeros
        if ((zeros1 > 0 && zeros2 == 0 && sum1 > sum2) || (zeros2 > 0 && zeros1 == 0 && sum2 > sum1)) return -1;

        long targetSum = Math.Max(sum1, sum2);
        long diff = Math.Abs(sum1 - sum2);

        // If there are zeros in both arrays, we can make the sums equal by adding the same amount to both sums.
        if (zeros1 > 0 && zeros2 > 0) {
            if (sum1 == sum2) return sum1 + Math.Max(zeros1, zeros2);
            return targetSum + Math.Max(zeros1, zeros2);
        }

        // If there are zeros in one array, we need to check if the difference can be evenly distributed among them.
        if (zeros1 > 0 || zeros2 > 0) {
            int totalZeros = zeros1 + zeros2;
            if (diff % totalZeros == 0) {
                return targetSum + Math.Max(zeros1, zeros2);
            }
        }

        // Handling cases where direct addition is possible to make sums equal
        if (sum1 == sum2) {
            return sum1 + Math.Max(zeros1, zeros2);
        }

        // If one sum is greater and has zeros, adjust by adding minimal amounts to the lesser sum to make them equal
        if (sum1 < sum2 && zeros1 > 0) {
            long additional = (sum2 - sum1) / zeros1;
            if ((sum2 - sum1) % zeros1 == 0) return sum2;
            else return sum2 + zeros1 - ((sum2 - sum1) % zeros1);
        }
        if (sum2 < sum1 && zeros2 > 0) {
            long additional = (sum1 - sum2) / zeros2;
            if ((sum1 - sum2) % zeros2 == 0) return sum1;
            else return sum1 + zeros2 - ((sum1 - sum2) % zeros2);
        }

        // If unable to make sums equal with above conditions, return -1
        return -1;
    }
}
