class Solution {
public:
    long long minSum(vector<int>& nums1, vector<int>& nums2) {
        long long sum1 = 0, sum2 = 0;
        for (auto num : nums1) sum1 += num;
        for (auto num : nums2) sum2 += num;

        int zeros1 = count(nums1.begin(), nums1.end(), 0);
        int zeros2 = count(nums2.begin(), nums2.end(), 0);

        if (sum1 == sum2) return sum1 + max(zeros1, zeros2);

        if ((sum1 > sum2 && zeros2 == 0) || (sum2 > sum1 && zeros1 == 0)) return -1;

        long long targetSum = max(sum1, sum2);
        long long minSumNeeded = targetSum - min(sum1, sum2);
        long long totalZeros = zeros1 + zeros2;

        if (minSumNeeded < totalZeros) {
            return targetSum + totalZeros - minSumNeeded;
        } else {
            return targetSum + totalZeros;
        }
    }
};
