class Solution {
public:
    long long minSum(vector<int>& nums1, vector<int>& nums2) {
        long long sum1 = 0, sum2 = 0;
        for (int num : nums1) sum1 += num;
        for (int num : nums2) sum2 += num;
        int countZeros1 = 0, countZeros2 = 0;
        for (int num : nums1) if (num == 0) ++countZeros1;
        for (int num : nums2) if (num == 0) ++countZeros2;

        if (sum1 == sum2) {
            // If sums are equal, the minimum sum is the current sum plus the number of zeros
            // since each zero must be replaced by at least 1.
            return sum1 + max(countZeros1, countZeros2);
        } else if ((countZeros1 == 0 && countZeros2 == 0) || (sum1 != sum2 && countZeros1 + countZeros2 == 0)) {
            // If there are no zeros in both arrays and sums are not equal, it's impossible.
            return -1;
        } else {
            // Calculate the difference and adjust for zeros.
            long long diff = abs(sum1 - sum2);
            // The goal is to make the sum equal by adding the minimum required to the array with the lesser sum.
            // The minimum addition is the difference plus one for each zero in the array with the lesser sum,
            // since each zero must be replaced by at least 1.
            if (sum1 < sum2) {
                // If the first array has the lesser sum, check if it's possible to balance the sums.
                return sum2 + countZeros1; // Add one for each zero in nums1.
            } else {
                // If the second array has the lesser sum, check if it's possible to balance the sums.
                return sum1 + countZeros2; // Add one for each zero in nums2.
            }
        }
    }
};
