long long minSum(int* nums1, int nums1Size, int* nums2, int nums2Size) {
    long long sum1 = 0, sum2 = 0;
    int zeros1 = 0, zeros2 = 0;

    // Calculate sum and count zeros for both arrays
    for (int i = 0; i < nums1Size; i++) {
        sum1 += nums1[i];
        if (nums1[i] == 0) zeros1++;
    }
    for (int i = 0; i < nums2Size; i++) {
        sum2 += nums2[i];
        if (nums2[i] == 0) zeros2++;
    }

    // If both arrays have no zeros, check if sums are already equal
    if (zeros1 == 0 && zeros2 == 0) {
        return sum1 == sum2 ? sum1 : -1;
    }

    // Find the difference in sums and calculate minimum sum
    long long diff = sum1 - sum2;

    // When one array has zeros and the other does not, or the difference cannot be equally divided among the zeros
    if ((zeros1 > 0 && zeros2 == 0 && diff <= 0) || (zeros2 > 0 && zeros1 == 0 && diff >= 0) || 
        (zeros1 > 0 && zeros2 > 0 && diff % (zeros1 + zeros2) != 0)) {
        return -1;
    }

    // Calculate minimum equal sum
    long long adjustment = (zeros1 + zeros2 > 0) ? abs(diff) / (zeros1 + zeros2) : 0;
    long long minSum = (diff == 0) ? sum1 : (diff > 0 ? sum1 + zeros2 * (adjustment + 1) : sum2 + zeros1 * (adjustment + 1));
    return minSum + adjustment * abs(diff % (zeros1 + zeros2));
}
