long long minSum(int* nums1, int nums1Size, int* nums2, int nums2Size) {
    long long sum1 = 0, sum2 = 0;
    int zeros1 = 0, zeros2 = 0;

    // Calculate sum and count zeros for both arrays
    for (int i = 0; i < nums1Size; i++) {
        sum1 += nums1[i];
        if (nums1[i] == 0) zeros1++;
    }
    for (int i = 0; i < nums2Size; i++) {
        sum2 += nums2[i];
        if (nums2[i] == 0) zeros2++;
    }

    if (zeros1 == 0 && zeros2 == 0 && sum1 != sum2) return -1;

    // Determine the total sum after replacing zeros to make arrays equal
    long long totalZeros = zeros1 + zeros2;
    long long totalSum = sum1 + sum2 + totalZeros; // Adding 1 for each zero
    if (totalSum % 2 != 0) return -1; // If total sum is odd, cannot split evenly

    long long targetSum = totalSum / 2;
    if ((targetSum < sum1 && zeros2 == 0) || (targetSum < sum2 && zeros1 == 0)) return -1;

    // Check if we can reach target sum by adding minimum numbers
    return targetSum >= sum1 && targetSum >= sum2 ? targetSum : -1;
}
