class Solution {
    public long minSum(int[] nums1, int[] nums2) {
        long sum1 = 0, sum2 = 0;
        int zeros1 = 0, zeros2 = 0;
        for (int num : nums1) {
            if (num == 0) zeros1++;
            sum1 += num;
        }
        for (int num : nums2) {
            if (num == 0) zeros2++;
            sum2 += num;
        }

        if (sum1 == sum2 && zeros1 == 0 && zeros2 == 0) return sum1; // No zeros and sums are already equal

        // The idea is to equalize the sums by adding the minimum required numbers to the arrays with zeros.
        // If one sum is greater than the other, we need to add at least that difference in the opposite array to make them equal.
        // However, if the sums are equal but there are zeros, we need to add at least 1 to each zero to keep the arrays' sums equal and positive.

        long totalNeeded = Math.abs(sum1 - sum2); // The initial difference needed to equalize sums
        int totalZeros = zeros1 + zeros2; // Total zeros in both arrays

        if (totalZeros == 0) return -1; // No way to make sums equal if no zeros and sums are different

        // If sums are already equal, distribute 1 to each zero
        if (sum1 == sum2) return sum1 + totalZeros;

        // If one array needs to catch up, check if it can with the available zeros
        long minSumToAdd = Math.max(sum1, sum2) - Math.min(sum1, sum2); // Minimum sum to add to the lesser sum to make it equal
        if (minSumToAdd > 0 && (zeros1 == 0 || zeros2 == 0)) return -1; // Cannot make up difference if no zeros in one array

        // Adjust the sum with minimum number of increments to make the sums equal
        long targetSum = Math.max(sum1, sum2) + Math.max(zeros1, zeros2); // Target sum after adding minimum required to zeros
        return targetSum;
    }
}
