/**
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @return {number}
 */
var minSum = function(nums1, nums2) {
    let sum1 = nums1.reduce((acc, val) => acc + val, 0);
    let sum2 = nums2.reduce((acc, val) => acc + val, 0);

    // No need to replace 0's if sums are already equal and no 0's in arrays
    if (sum1 === sum2 && !nums1.includes(0) && !nums2.includes(0)) {
        return sum1;
    }

    let zeros1 = nums1.reduce((acc, val) => val === 0 ? acc + 1 : acc, 0);
    let zeros2 = nums2.reduce((acc, val) => val === 0 ? acc + 1 : acc, 0);

    // Calculate the total minimum sum needed to make both arrays equal
    let totalMinSumNeeded = Math.abs(sum1 - sum2);
    // Total number of replacements needed (each 0 replaced by at least 1)
    let totalReplacementsNeeded = zeros1 + zeros2;

    if (sum1 === sum2) {
        // If initial sums are equal, minimum sum is the current sum plus the total number of 0's (each replaced by 1)
        return sum1 + totalReplacementsNeeded;
    } else if (sum1 > sum2) {
        if (zeros2 === 0) {
            return -1; // Impossible to equalize without zeros in the smaller array
        }
        // Calculate new sum considering replacements to equalize and exceed the difference
        let newSum = sum2 + zeros2 + (totalMinSumNeeded + (zeros1 - zeros2));
        return newSum;
    } else { // sum2 > sum1
        if (zeros1 === 0) {
            return -1; // Impossible to equalize without zeros in the smaller array
        }
        // Calculate new sum considering replacements to equalize and exceed the difference
        let newSum = sum1 + zeros1 + (totalMinSumNeeded + (zeros2 - zeros1));
        return newSum;
    }
};
