class Solution {
    fun minSum(nums1: IntArray, nums2: IntArray): Long {
        var sum1 = nums1.sum()
        var sum2 = nums2.sum()
        var zeroCount1 = nums1.count { it == 0 }
        var zeroCount2 = nums2.count { it == 0 }

        if (zeroCount1 == 0 && zeroCount2 == 0) {
            return if (sum1 == sum2) sum1.toLong() else -1
        }

        val totalZeros = zeroCount1 + zeroCount2
        val diff = kotlin.math.abs(sum1 - sum2)

        if ((diff % totalZeros) != 0) return -1
        val valueToAdd = diff / totalZeros

        sum1 += zeroCount1 * valueToAdd
        sum2 += zeroCount2 * valueToAdd

        return if (sum1 == sum2) sum1.toLong() else -1
    }
}
