class Solution {
    fun minSum(nums1: IntArray, nums2: IntArray): Long {
        var sum1 = nums1.sumOf { it.toLong() }
        var sum2 = nums2.sumOf { it.toLong() }
        val diff = kotlin.math.abs(sum1 - sum2)
        val zerosCount1 = nums1.count { it == 0 }
        val zerosCount2 = nums2.count { it == 0 }

        if (zerosCount1 + zerosCount2 == 0) {
            return if (sum1 == sum2) sum1 else -1
        }

        // Ensure sum1 is always the smaller sum for consistency
        if (sum1 > sum2) {
            val temp = sum1
            sum1 = sum2
            sum2 = temp
        }

        val totalZeros = zerosCount1 + zerosCount2
        // The target sum after adding the minimum necessary values to make the sums equal
        val targetSum = maxOf(sum1 + zerosCount1, sum2 + zerosCount2)

        // If after adding 1 to all zeros in the smaller array, its sum exceeds the target, or it's not possible to make them equal
        if (sum1 + zerosCount1 > targetSum || (targetSum * totalZeros - sum1 - sum2) % totalZeros != 0L) {
            return -1
        }

        return targetSum
    }
}
