class Solution {

    /**
     * @param Integer[] $nums1
     * @param Integer[] $nums2
     * @return Integer
     */
    function minSum($nums1, $nums2) {
        $sum1 = array_sum($nums1);
        $sum2 = array_sum($nums2);
        $zeros1 = count(array_filter($nums1, function($v) { return $v === 0; }));
        $zeros2 = count(array_filter($nums2, function($v) { return $v === 0; }));

        if ($sum1 == $sum2 && $zeros1 == 0 && $zeros2 == 0) {
            return $sum1;
        }

        // Calculate the minimum sum needed to balance both arrays
        $totalZeros = $zeros1 + $zeros2;
        if ($sum1 == $sum2 && $totalZeros > 0) {
            return $sum1 + $totalZeros;
        }

        $diff = abs($sum1 - $sum2);
        if ($zeros1 + $zeros2 == 0) {
            return -1; // No zeros to replace and sums are different
        }

        if (($sum1 + $zeros1) == ($sum2 + $zeros2)) {
            return $sum1 + $zeros1;
        }

        // If the difference can be divided equally among the zeros, then return the sum + difference
        if ($sum1 > $sum2) {
            if (($diff + $zeros2) % $totalZeros == 0) {
                return $sum1 + $zeros1;
            }
        } else {
            if (($diff + $zeros1) % $totalZeros == 0) {
                return $sum2 + $zeros2;
            }
        }

        // Calculate the minimum value to make the sums equal
        $minValue = max($diff / $totalZeros, 1);
        if (floor($minValue) != $minValue) {
            return -1; // Cannot evenly distribute the difference
        }

        return max($sum1 + $zeros1 * $minValue, $sum2 + $zeros2 * $minValue);
    }
}
