object Solution {
    def minSum(nums1: Array[Int], nums2: Array[Int]): Long = {
        val sum1 = nums1.filter(_ > 0).sum
        val sum2 = nums2.filter(_ > 0).sum
        val zeros1 = nums1.count(_ == 0)
        val zeros2 = nums2.count(_ == 0)
        if (sum1 == sum2 && zeros1 == zeros2) {
            return (sum1 + zeros1).toLong
        } else if ((sum1 != sum2) && (zeros1 == 0 || zeros2 == 0) && (zeros1 != zeros2)) {
            return -1
        }
        val totalZeros = zeros1 + zeros2
        var minSum = sum1.max(sum2)
        var diff = (sum1 - sum2).abs
        if (diff % totalZeros == 0) {
            minSum += diff
        } else {
            minSum += diff + totalZeros - (diff % totalZeros)
        }
        minSum
    }
}
