public class Solution {
    public long MinIncrementOperations(int[] nums, int k) {
        long operations = 0;
        int maxInWindow = 0;
        for (int i = 0; i < nums.Length; i++) {
            if (i < 2) {
                maxInWindow = Math.Max(maxInWindow, nums[i]);
                continue;
            }
            maxInWindow = Math.Max(maxInWindow, nums[i]);
            if (maxInWindow < k) {
                operations += k - maxInWindow;
                nums[i] += k - maxInWindow;
                maxInWindow = k;
            }
            if (i >= 2) maxInWindow = Math.Max(nums[i], Math.Max(nums[i - 1], nums[i - 2]));
        }
        return operations;
    }
}
