class Solution {
public:
    long long minIncrementOperations(vector<int>& nums, int k) {
        long long operations = 0;
        for (int i = 0; i < nums.size(); ++i) {
            int maxInSubarray = nums[i];
            for (int j = i; j < min(i + 3, (int)nums.size()); ++j) {
                maxInSubarray = max(maxInSubarray, nums[j]);
            }
            if (maxInSubarray < k) {
                operations += k - maxInSubarray;
                for (int j = i; j < min(i + 3, (int)nums.size()); ++j) {
                    nums[j] = max(nums[j], k - (maxInSubarray - nums[j]));
                }
            }
        }
        return operations;
    }
};
