class Solution {
public:
    long long minIncrementOperations(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        for (int i = 0; i < n; ++i) {
            if (nums[i] < k) {
                int maxReach = min(n, i + 3);
                int maxVal = nums[i];
                for (int j = i; j < maxReach; ++j) {
                    maxVal = max(maxVal, nums[j]);
                }
                if (maxVal < k) {
                    ans += k - maxVal;
                    for (int j = i; j < maxReach; ++j) {
                        nums[j] += k - maxVal;
                    }
                }
            }
        }
        return ans;
    }
};
