class Solution {
public:
    long long minIncrementOperations(vector<int>& nums, int k) {
        long long ops = 0;
        int maxElem = *max_element(nums.begin(), nums.end());
        if (maxElem >= k) return 0; // No operations needed if max element is already >= k

        // Increase each element to make sure every subarray of size >= 3 contains an element >= k
        for (int i = 0; i < nums.size(); i++) {
            if (nums[i] < k) {
                // Calculate operations needed to increase current element to make subarray beautiful
                ops += k - nums[i];
                nums[i] = k;
            }
        }

        return ops;
    }
};
