class Solution {
    public long minIncrementOperations(int[] nums, int k) {
        long increments = 0;
        for (int i = 0; i < nums.length; i++) {
            if (i + 2 < nums.length) { // Check if a subarray of size 3 or more starts at this index
                int max = Math.max(nums[i], Math.max(nums[i + 1], nums[i + 2]));
                if (max < k) { // If max of this subarray is less than k
                    increments += k - max; // Increment to make max equal to k
                    nums[i] = nums[i] + (k - max); // Update the current element to reflect the increment
                }
            }
        }
        return increments;
    }
}
