/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minIncrementOperations = function(nums, k) {
    let operations = 0;
    for (let i = 0; i < nums.length; i++) {
        if (i + 2 < nums.length) {
            const maxInSubarray = Math.max(nums[i], nums[i + 1], nums[i + 2]);
            if (maxInSubarray < k) {
                const incrementNeeded = k - maxInSubarray;
                operations += incrementNeeded;
                nums[i + 2] += incrementNeeded; // Always increment the last element of the subarray for optimal solution
            }
        }
    }
    return operations;
};
