class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer
     */
    function minIncrementOperations($nums, $k) {
        $count = count($nums);
        $increments = 0;

        for ($i = 0; $i < $count; $i++) {
            if ($nums[$i] < $k) {
                // For each element, check if the next two elements make it part of a subarray needing increments
                $maxInSubarray = $nums[$i];
                for ($j = $i + 1; $j < $i + 3 && $j < $count; $j++) {
                    $maxInSubarray = max($maxInSubarray, $nums[$j]);
                }
                // If the max in the current subarray is less than k, increment this and future elements in the subarray
                if ($maxInSubarray < $k) {
                    $increment = $k - $maxInSubarray;
                    $increments += $increment;
                    for ($j = $i; $j < $i + 3 && $j < $count; $j++) {
                        $nums[$j] += $increment;
                    }
                }
            }
        }

        return $increments;
    }
}
