class Solution {

    /**
     * @param Integer[] $nums
     * @param Integer $k
     * @return Integer
     */
    function minIncrementOperations($nums, $k) {
        $incrementCount = 0;
        $n = count($nums);

        // Calculate the increments needed for each number if it's part of a subarray of size 3 or more
        for ($i = 0; $i < $n; $i++) {
            // Only check subarrays where the current number could be the max
            if ($i < $n - 2) { // Ensure there are at least 3 elements in the subarray
                $maxNeeded = $k - max($nums[$i], $nums[$i + 1], $nums[$i + 2]);
                if ($maxNeeded > 0) {
                    // If max of any 3 consecutive numbers is less than k, increment the third number
                    $incrementCount += $maxNeeded;
                    $nums[$i + 2] += $maxNeeded;
                }
            }
        }

        return $incrementCount;
    }
}
