class Solution(object):
    def minIncrementOperations(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        max_val = max(nums)
        if max_val >= k:
            return 0

        increments = 0
        for i in range(len(nums)):
            if nums[i] < k:
                increments += k - nums[i] - 1
                nums[i] = k - 1

        nums.sort()
        max_val = nums[-1]
        increments += (k - 1 - max_val) * len(nums)
        for i in range(len(nums) - 2, -1, -1):
            if nums[i] < k - 1:
                increments += k - 1 - nums[i]
                nums[i] = k - 1

        return increments
