from typing import List

class Solution:
    def minIncrementOperations(self, nums: List[int], k: int) -> int:
        operations, max_in_window = 0, 0
        window = []

        for num in nums:
            window.append(num)
            if len(window) > 3:
                window.pop(0)
            max_in_window = max(max_in_window, num, max(window[-3:]))
            if len(window) == 3 and max_in_window < k:
                diff = k - max_in_window
                operations += diff
                max_in_window += diff
                window[-1] += diff

        return operations
