impl Solution {
    pub fn min_increment_operations(nums: Vec<i32>, k: i32) -> i64 {
        let n = nums.len();
        let mut increments = 0i64;
        let mut required_increments = vec![0; n];

        for i in 0..n {
            if i >= 2 {
                required_increments[i] = required_increments[i].max(required_increments[i - 2]);
            }
            if nums[i] + required_increments[i] < k {
                increments += (k - (nums[i] + required_increments[i])) as i64;
                required_increments[i] += k - (nums[i] + required_increments[i]);
            }
        }

        increments
    }
}
