class Solution {
    func minIncrementOperations(_ nums: [Int], _ k: Int) -> Int {
        var ops = 0
        var maxInWindow = 0
        var increments = Array(repeating: 0, count: nums.count)

        for i in 0..<nums.count {
            if i > 0 {
                increments[i] += increments[i - 1]
            }

            let current = nums[i] + increments[i]
            maxInWindow = max(maxInWindow, current)

            if i >= 2 {
                if maxInWindow < k {
                    let diff = k - maxInWindow
                    ops += diff
                    increments[i] += diff
                    maxInWindow = k
                }
                if nums[i - 2] + increments[i - 2] == maxInWindow {
                    maxInWindow = max(nums[i - 1] + increments[i - 1], nums[i] + increments[i])
                    for j in i - 1...i {
                        maxInWindow = max(maxInWindow, nums[j] + increments[j])
                    }
                }
            }
        }
        return ops
    }
}
