function minIncrementOperations(nums: number[], k: number): number {
    let operations = 0;

    // Sort the array to make it easier to find the minimum increments needed
    nums.sort((a, b) => a - b);

    for (let i = 0; i < nums.length - 2; i++) {
        // For every triplet starting with i, check if the max value is less than k
        if (nums[i + 2] < k) {
            // Calculate increments needed to make the current maximum at least k
            operations += k - nums[i + 2];
            // Apply the increments to the maximum of the current triplet
            nums[i + 2] = k;
        }
    }

    return operations;
};
