function minIncrementOperations(nums: number[], k: number): number {
    let increments = 0;

    // Find the maximum element in the array
    let max = Math.max(...nums);

    // If the maximum element is already greater than or equal to k, no operations are needed
    if (max >= k) return 0;

    // Sort the array in non-decreasing order to work with elements in order
    nums.sort((a, b) => a - b);

    for (let i = nums.length - 1; i >= 2; i--) {
        // Work backwards from the end of the array
        if (nums[i] < k) {
            // If the current element is less than k, calculate how many increments are needed
            let diff = k - nums[i];
            increments += diff;
            // Apply the increments to make the current maximum at least k
            nums[i] += diff;

            // Ensure that the increments are also reflected in the previous two elements of the subarray,
            // so that the property holds for all subarrays of size 3 or more
            nums[i - 1] += diff;
            nums[i - 2] += diff;
        }
    }

    return increments;
}
