class Solution {
public:
    int minimumIndex(vector<int>& nums) {
        int n = nums.size();
        unordered_map<int, int> freq;
        int dominant = nums[0], maxFreq = 1;
        for (int num : nums) {
            ++freq[num];
            if (freq[num] > maxFreq) {
                dominant = num;
                maxFreq = freq[num];
            }
        }

        vector<int> prefixFreq(n, 0), suffixFreq(n, 0);
        int count = 0;
        for (int i = 0; i < n; ++i) {
            if (nums[i] == dominant) ++count;
            prefixFreq[i] = count;
        }
        count = 0;
        for (int i = n - 1; i >= 0; --i) {
            if (nums[i] == dominant) ++count;
            suffixFreq[i] = count;
        }

        for (int i = 0; i < n - 1; ++i) {
            if (prefixFreq[i] * 2 > (i + 1) && suffixFreq[i + 1] * 2 > (n - i - 1)) {
                return i;
            }
        }

        return -1;
    }
};
