int minimumIndex(int* nums, int numsSize) {
    // Finding the dominant element and its frequency in the whole array
    int dominant = nums[0], maxFreq = 1, currentFreq = 1;
    for (int i = 1; i < numsSize; ++i) {
        if (nums[i] == nums[i - 1]) {
            ++currentFreq;
        } else {
            currentFreq = 1;
        }
        if (currentFreq > maxFreq) {
            maxFreq = currentFreq;
            dominant = nums[i];
        }
    }

    int freq = 0;
    for (int i = 0; i < numsSize; ++i) {
        if (nums[i] == dominant) ++freq;
    }

    // Checking for the minimum index where the split is valid
    int leftFreq = 0;
    for (int i = 0; i < numsSize - 1; ++i) {
        if (nums[i] == dominant) ++leftFreq;
        int rightFreq = freq - leftFreq;
        if (leftFreq * 2 > i + 1 && rightFreq * 2 > numsSize - i - 1) {
            return i;
        }
    }

    return -1;
}
