func minChanges(s string) int {
    count0, count1 := 0, 0 // Count for changes to make all characters at even indices 0 or 1
    for i := 0; i < len(s); i += 2 {
        if s[i] == '0' {
            count1++
        } else {
            count0++
        }
    }

    // Since the string length is even, the other half must be the opposite character
    // No need to iterate over the string again, use total length / 2 for the other half
    total := len(s) / 2
    return min(count0, count1) + min(total-count0, total-count1)
}

func min(a, b int) int {
    if a < b {
        return a
    }
    return b
}
