object Solution {
    def minChanges(s: String): Int = {
        var even0 = 0 // Count of 0s at even indices
        var even1 = 0 // Count of 1s at even indices
        var odd0 = 0  // Count of 0s at odd indices
        var odd1 = 0  // Count of 1s at odd indices

        for (i <- s.indices) {
            if (i % 2 == 0) {
                if (s(i) == '0') even0 += 1 else even1 += 1
            } else {
                if (s(i) == '0') odd0 += 1 else odd1 += 1
            }
        }

        // To make the string beautiful, one type of digit (0 or 1) should dominate each position (even or odd)
        // Min changes = Total length - max(even dominance, odd dominance)
        Math.min(even0 + odd1, even1 + odd0)
    }
}
