class Solution {
public:
    int minimumCoins(vector<int>& prices) {
        int n = prices.size();
        vector<int> dp(n + 1, 1e9); // Initialize DP array with a large value
        dp[0] = 0; // Base case: 0 coins needed to buy 0 fruits
        for (int i = 0; i < n; i++) {
            int sum = 0;
            for (int j = i; j < n; j++) {
                sum += prices[j]; // Sum of prices from i to j
                int free = min(n, j + 1 + (j - i + 1)); // Calculate the index up to which fruits are free
                dp[free] = min(dp[free], dp[i] + sum); // Update DP value for acquiring free fruits
            }
        }
        return dp[n]; // Minimum coins needed to buy all fruits
    }
};
