int minimumCoins(int* prices, int pricesSize) {
    int totalCoins = 0, i = 0;
    while (i < pricesSize) {
        totalCoins += prices[i];
        i += prices[i] + 1;
        if (i >= pricesSize) break;
        // Check for a cheaper option in the next i fruits
        int minPrice = prices[i];
        for (int j = i + 1; j < pricesSize && j < i + prices[i]; ++j) {
            if (prices[j] < minPrice) {
                minPrice = prices[j];
                i = j; // Update i to purchase the cheaper fruit
            }
        }
    }
    return totalCoins;
}
