#include <stdlib.h> // Required for qsort

int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int minimumAddedCoins(int* coins, int coinsSize, int target) {
    long long maxReachable = 0;
    int addedCoins = 0;
    int i = 0;

    qsort(coins, coinsSize, sizeof(int), compare);

    while (maxReachable < target) {
        if (i < coinsSize && coins[i] <= maxReachable + 1) {
            maxReachable += coins[i];
            i++;
        } else {
            maxReachable = 2 * maxReachable + 1;
            addedCoins++;
        }
    }

    return addedCoins;
}
