class Solution {

    /**
     * @param Integer[] $coins
     * @param Integer $target
     * @return Integer
     */
    function minimumAddedCoins($coins, $target) {
        sort($coins);
        $addedCoins = 0;
        $currentMax = 0;
        $i = 0;
        while ($currentMax < $target) {
            if ($i < count($coins) && $coins[$i] <= $currentMax + 1) {
                $currentMax += $coins[$i];
                $i++;
            } else {
                $currentMax = 2 * $currentMax + 1;
                $addedCoins++;
            }
        }
        return $addedCoins;
    }
}
