class Solution {
public:
    int minGroupsForValidAssignment(vector<int>& nums) {
        unordered_map<int, int> freq;
        for (int num : nums) {
            ++freq[num];
        }
        priority_queue<int, vector<int>, greater<int>> pq;
        for (auto& [_, count] : freq) {
            pq.push(count);
        }
        int groups = 0;
        while (!pq.empty()) {
            int top = pq.top();
            pq.pop();
            ++groups;
            if (!pq.empty() && pq.top() == top) {
                pq.pop();
                if (top - 1 > 0) pq.push(top - 1);
            }
        }
        return groups;
    }
};
