func minGroupsForValidAssignment(nums []int) int {
    freq := make(map[int]int)
    for _, num := range nums {
        freq[num]++
    }

    var counts []int
    for _, count := range freq {
        counts = append(counts, count)
    }

    sort.Ints(counts)

    left, right := 0, len(nums)
    for left < right {
        mid := (left + right) / 2
        if isValid(counts, mid) {
            right = mid
        } else {
            left = mid + 1
        }
    }

    return left
}

func isValid(counts []int, groups int) bool {
    requiredGroups := 0
    for i := len(counts) - 1; i >= 0; i-- {
        requiredGroups += (counts[i] + groups - 1) / groups
    }
    return requiredGroups <= groups
}
