class Solution {
    public int minGroupsForValidAssignment(int[] nums) {
        int[] count = new int[1000001]; // Considering the maximum value of nums[i] is 10^9
        for (int num : nums) {
            count[num]++;
        }
        int prev = 0, groups = 0;
        for (int c : count) {
            if (c > 0) {
                int diff = Math.abs(c - prev);
                if (diff > 1) {
                    groups += (c + prev) / 2;
                    prev = (c + prev) % 2;
                } else {
                    groups += diff;
                    prev = Math.min(c, prev);
                }
            }
        }
        return groups + (prev > 0 ? 1 : 0);
    }
}
