function minGroupsForValidAssignment(nums: number[]): number {
    const freqMap = new Map<number, number>();
    nums.forEach(num => {
        freqMap.set(num, (freqMap.get(num) || 0) + 1);
    });

    const frequencies = Array.from(freqMap.values()).sort((a, b) => a - b);
    let groups = 0, i = 0;

    while (i < frequencies.length) {
        let count = 1, j = i + 1;
        while (j < frequencies.length && frequencies[j] === frequencies[i]) {
            count++;
            j++;
        }
        groups = Math.max(groups, Math.ceil(count / 2));
        i = j;
    }

    return groups;
};
