function minOperations(nums: number[]): number {
    const countMap = new Map<number, number>();
    nums.forEach(num => {
        countMap.set(num, (countMap.get(num) || 0) + 1);
    });

    let operations = 0;
    let remainTwo = 0;
    for (const [_, count] of countMap) {
        operations += Math.floor(count / 3);
        const remain = count % 3;
        if (remain === 2) remainTwo++;
    }

    const totalPairs = Math.floor(nums.length / 3) * 2 + remainTwo;
    if (nums.length % 3 === 0 && totalPairs % 2 === 0) return operations + totalPairs / 2;
    if (nums.length % 3 === 1 && (totalPairs - 1) % 2 === 0) return operations + (totalPairs - 1) / 2;
    if (nums.length % 3 === 2 && totalPairs % 2 === 0) return operations + totalPairs / 2;

    return -1;
};
