class Solution {
  int minOperations(List<int> nums, int k) {
    int xorSum = nums.fold(0, (previousValue, element) => previousValue ^ element);
    if (xorSum == k) return 0;
    int maxBit = 0;
    for (int num in nums) {
      int currentMaxBit = num.bitLength;
      if (currentMaxBit > maxBit) {
        maxBit = currentMaxBit;
      }
    }
    int kBitLength = k.bitLength;
    if (maxBit < kBitLength) maxBit = kBitLength;

    List<int> bitCounts = List.filled(maxBit, 0);
    for (int num in nums) {
      for (int i = 0; i < maxBit; i++) {
        if (((num >> i) & 1) == 1) {
          bitCounts[i]++;
        }
      }
    }

    int operations = 0;
    for (int i = 0; i < maxBit; i++) {
      if (((xorSum >> i) & 1) != ((k >> i) & 1)) {
        operations += min(bitCounts[i], nums.length - bitCounts[i]);
      }
    }
    return operations;
  }

  int min(int a, int b) => (a < b) ? a : b;
}
