function minOperations(nums: number[], k: number): number {
    let xorSum = nums.reduce((acc, val) => acc ^ val, 0) ^ k;
    if (xorSum === 0) return 0; // No operation needed if already equal to k

    // Count the frequency of bits set at each position for all numbers
    let bitFrequency = new Array(32).fill(0);
    nums.forEach(num => {
        for (let i = 0; i < 32; i++) {
            if ((num & (1 << i)) !== 0) bitFrequency[i]++;
        }
    });

    let operations = 0;
    for (let i = 31; i >= 0; i--) {
        if ((xorSum & (1 << i)) !== 0) {
            if (bitFrequency[i] > 0) { // If there's at least one bit to flip
                operations = Math.max(operations, 1);
                if (bitFrequency[i] < nums.length) return operations; // If not all bits are set, just one operation is enough
            }
        }
    }

    return -1; // If no such operation can lead to the desired result
}
