public class Solution {
    public int MinimumOperations(string num) {
        int minOps = num.Length; // Start with the worst-case scenario
        // Special numbers end with 00, 25, 50, 75
        string[] specials = { "00", "25", "50", "75" };

        for (int s = 0; s < specials.Length; s++) {
            int ops = 0;
            int j = num.Length - 1;
            for (int i = 1; i >= 0; i--) {
                while (j >= 0 && num[j] != specials[s][i]) {
                    j--;
                    ops++;
                }
                if (j >= 0) j--;
                else {
                    ops = int.MaxValue;
                    break;
                }
            }
            minOps = Math.Min(minOps, ops);
        }

        // Handling the case when num becomes 0 (i.e., when num has no digits)
        if (minOps == int.MaxValue || num == "0") return num.Length;
        return minOps + (num.Length - 2); // Removing digits to match specials + adjusting for 0-based index
    }
}
